import React, { useEffect, useState } from "react";
import axios from "axios";

const LawsPage = () => {
  const [laws, setLaws] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedActType, setSelectedActType] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch laws data from the API
  useEffect(() => {
    const fetchLaws = async () => {
      try {
        const response = await axios.get(
          "https://sih-backend-nine.vercel.app/data/"
        );
        console.log(response.data); // Log the response for debugging
        setLaws(response.data.data); // Update state with fetched laws
        setLoading(false);
      } catch (err) {
        console.error("Error fetching laws:", err);
        setError("Failed to fetch laws data.");
        setLoading(false);
      }
    };

    fetchLaws();
  }, []);

  // Filter laws based on search query and selected act type
  const filteredLaws = searchQuery
    ? laws.filter(
        (law) =>
          (selectedActType ? law.actType === selectedActType : true) &&
          (law.section.toLowerCase().includes(searchQuery.toLowerCase()) ||
            law.title.toLowerCase().includes(searchQuery.toLowerCase()))
      )
    : selectedActType
    ? laws.filter((law) => law.actType === selectedActType)
    : laws;

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-4xl font-bold text-center text-blue-900 mb-8">
        Laws and Acts Search
      </h1>

      <div className="flex justify-center mb-8 space-x-4">
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          placeholder="Search for laws..."
          className="p-3 border border-gray-300 rounded-lg w-1/2"
        />
        <select
          value={selectedActType}
          onChange={(e) => setSelectedActType(e.target.value)}
          className="p-3 border border-gray-300 rounded-lg"
        >
          <option value="">All Act Types</option>
          <option value="Criminal">Criminal</option>
          <option value="Civil">Civil</option>
          <option value="Family">Family</option>
          {/* Add more options as needed */}
        </select>
      </div>

      {loading && (
        <div className="text-center text-lg text-blue-800">
          Loading laws data...
        </div>
      )}

      {error && (
        <div className="text-center text-lg text-red-600">
          {error} Please try again later.
        </div>
      )}

      {!loading && !error && (
        <div className="all-laws bg-gray-50 p-8 rounded-lg shadow-md border border-gray-200">
          <h3 className="text-3xl font-semibold text-blue-900 mb-6">
            Bare Acts
          </h3>
          {filteredLaws.length > 0 ? (
            <div className="space-y-6">
              {filteredLaws.map((law, index) => (
                <div
                  key={index}
                  className="law-item p-6 bg-white border border-gray-300 rounded-xl shadow-md"
                >
                  <h4 className="law-title text-2xl font-semibold text-blue-800 mb-2">
                    {law.actType} - {law.section}
                  </h4>
                  <p className="text-lg font-medium text-gray-700 mb-2">
                    {law.title}
                  </p>
                  <p className="text-gray-600">{law.description}</p>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-gray-500 italic mt-4">
              No laws found. Try a different search query.
            </p>
          )}
        </div>
      )}
    </div>
  );
};

export default LawsPage;
