import React, { useState, useEffect } from 'react';
import Sidebar from '../components/Sidebar'; // Import Sidebar component
import MenuBar from '../components/MenuBar'; // Import MenuBar component
import Footer from '../components/Footer'; // Import Footer component
import { FaBell, FaLanguage, FaFileAlt } from 'react-icons/fa'; // Import icons for settings

const Settings = () => {
  const [notifications, setNotifications] = useState(true);
  const [language, setLanguage] = useState('English');
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768); // State to track screen size

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768); // Update isMobile on window resize
    };

    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const handleNotificationsChange = () => {
    setNotifications(!notifications);
  };

  const handleLanguageChange = (e) => {
    setLanguage(e.target.value);
  };

  return (
    <div className="settings-container min-h-screen flex flex-col bg-gray-50">
      {/* Conditionally render Sidebar or MenuBar based on screen size */}
      {isMobile ? <MenuBar /> : <Sidebar />}

      <main className="main-content flex-grow p-8">
        <h2 className="settings-title text-4xl font-semibold text-gray-800 mb-10 mt-14 text-center">
          Settings
        </h2>
        <p className="settings-description text-lg text-gray-600 mb-12 text-center">
          Customize your preferences and application settings here.
        </p>

        <div className="settings-cards grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {/* Notifications Card */}
          <div className="setting-card bg-white p-6 rounded-lg shadow-lg hover:shadow-2xl transition-shadow duration-300">
            <div className="flex items-center space-x-3 mb-4">
              <FaBell className="text-2xl text-blue-600" />
              <h3 className="text-xl font-semibold text-gray-800">Notifications</h3>
            </div>
            <p className="text-gray-600 text-sm mb-4">
              Enable or disable notifications for app updates, messages, and more.
            </p>
            <label className="setting-label flex items-center space-x-3">
              <input
                type="checkbox"
                checked={notifications}
                onChange={handleNotificationsChange}
                className="setting-checkbox rounded-md border-gray-300 text-blue-600 focus:ring-blue-500"
                aria-label="Enable or disable notifications"
              />
              <span className="text-gray-700">Enable Notifications</span>
            </label>
          </div>

          {/* Language Card */}
          <div className="setting-card bg-white p-6 rounded-lg shadow-lg hover:shadow-2xl transition-shadow duration-300">
            <div className="flex items-center space-x-3 mb-4">
              <FaLanguage className="text-2xl text-green-600" />
              <h3 className="text-xl font-semibold text-gray-800">Language</h3>
            </div>
            <p className="text-gray-600 text-sm mb-4">
              Select your preferred language for the application interface.
            </p>
            <select
              id="language"
              value={language}
              onChange={handleLanguageChange}
              className="setting-select w-full p-3 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500"
              aria-label="Select language for the application"
            >
              <option value="English">English</option>
              <option value="Spanish">Spanish</option>
              <option value="French">French</option>
              {/* Add more languages as needed */}
            </select>
          </div>

          {/* Case Templates Card */}
          <div className="setting-card bg-white p-6 rounded-lg shadow-lg hover:shadow-2xl transition-shadow duration-300">
            <div className="flex items-center space-x-3 mb-4">
              <FaFileAlt className="text-2xl text-yellow-600" />
              <h3 className="text-xl font-semibold text-gray-800">Case Templates</h3>
            </div>
            <p className="text-gray-600 text-sm mb-4">
              Set your preferred default templates for creating new cases.
            </p>
            <select
              id="case-templates"
              className="setting-select w-full p-3 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500"
              aria-label="Select default case template"
            >
              <option value="template1">Template 1</option>
              <option value="template2">Template 2</option>
              {/* Add more templates as needed */}
            </select>
          </div>
        </div>
      </main>

      <Footer />
    </div>
  );
};

export default Settings;
