import React, { useState, useEffect } from 'react';
import axios from 'axios';

const LawsComponent = () => {
  const [laws, setLaws] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedActType, setSelectedActType] = useState(''); // Add this state for filtering act types

  // Fetch laws data from the API
  useEffect(() => {
    const fetchLaws = async () => {
      setLoading(true);
      try {
        const response = await axios.get("https://sih-backend-nine.vercel.app/data/");
        console.log("API Response:", response); // Log the full response object
        console.log("Data in Response:", response.data); // Log the "data" field

        // Check the data structure
        if (response.data && response.data.data && Array.isArray(response.data.data)) {
          console.log("Laws data:", response.data.data); // Log the laws data array
          setLaws(response.data.data); // Set laws state if the structure is valid
        } else {
          throw new Error("Invalid data structure received from API. Expected 'data' field to be an array.");
        }
      } catch (err) {
        console.error("Error fetching laws:", err.message || err);
        setError(`Failed to fetch laws data: ${err.message || err}`);
      } finally {
        setLoading(false);
      }
    };

    fetchLaws();
  }, []);

  // Filter laws based on search query and selected act type
  const filteredLaws = searchQuery
    ? laws.filter(
        (law) =>
          (selectedActType ? law.actType === selectedActType : true) &&
          (law.section.includes(searchQuery) ||
            law.title.toLowerCase().includes(searchQuery.toLowerCase()))
      )
    : selectedActType
    ? laws.filter((law) => law.actType === selectedActType)
    : laws;

  return (
    <div className="laws-container">
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search laws"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="search-input"
        />
        <select
          value={selectedActType}
          onChange={(e) => setSelectedActType(e.target.value)}
          className="act-type-select"
        >
          <option value="">All Act Types</option>
          <option value="IPC">IPC</option>
          <option value="CrPC">CrPC</option>
          <option value="Evidence Act">Evidence Act</option>
        </select>
      </div>

      <div className="all-laws bg-gray-50 p-8 rounded-lg shadow-md border border-gray-200">
        <h3 className="text-3xl font-semibold text-blue-900 mb-6">
          {searchQuery
            ? `Search Results (${filteredLaws.length})`
            : `Bare Acts (${laws.length})`}
        </h3>

        {loading ? (
          <p className="text-gray-500 italic">Loading...</p>
        ) : error ? (
          <p className="text-red-500 italic">{error}</p>
        ) : laws.length > 0 ? (
          <div className="space-y-6">
            {filteredLaws.map((law) => (
              <div
                key={law.section}
                className="law-item p-6 bg-white border border-gray-300 rounded-xl shadow-md hover:shadow-xl transition-transform duration-300 transform hover:scale-105 border-l-[4px] border-l-blue-500"
              >
                <h4 className="law-title text-2xl font-semibold text-blue-800 mb-2">
                  {law.actType} - {law.section}
                </h4>
                <p className="text-lg font-medium text-gray-700 mb-2">
                  {law.title}
                </p>
                <p className="text-gray-600">{law.description}</p>
              </div>
            ))}
          </div>
        ) : (
          <p className="text-gray-500 italic mt-4">
            No laws found. Try a different search query.
          </p>
        )}
      </div>
    </div>
  );
};

export default LawsComponent;
